import React, { useState, useRef } from "react";
import "./App.css";

const API_BASE = "http://localhost:8000"; // Change if backend runs elsewhere

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [audioUrl, setAudioUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [userInfo, setUserInfo] = useState(null);
  const fileInputRef = useRef();

  // Fetch user info on mount
  React.useEffect(() => {
    fetch(`${API_BASE}/user_info`)
      .then(res => res.json())
      .then(setUserInfo)
      .catch(() => {});
  }, []);

  const sendText = async () => {
    if (!input.trim()) return;
    setLoading(true);
    setMessages(msgs => [...msgs, { role: "user", content: input }]);
    setInput("");
    try {
      const res = await fetch(`${API_BASE}/chat`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ user_input: input, input_type: "text" })
      });
      const data = await res.json();
      setMessages(msgs => [...msgs, { role: "assistant", content: data.answer }]);
      setAudioUrl(data.audio_url ? `${API_BASE}${data.audio_url}` : null);
    } catch (e) {
      alert("Error communicating with server.");
    }
    setLoading(false);
  };

  const sendVoice = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setLoading(true);
    setMessages(msgs => [...msgs, { role: "user", content: "[Voice message sent]" }]);
    const formData = new FormData();
    formData.append("file", file);
    try {
      const res = await fetch(`${API_BASE}/voice`, {
        method: "POST",
        body: formData
      });
      const data = await res.json();
      setMessages(msgs => [...msgs, { role: "assistant", content: data.answer }]);
      setAudioUrl(data.audio_url ? `${API_BASE}${data.audio_url}` : null);
    } catch (e) {
      alert("Error communicating with server.");
    }
    setLoading(false);
    fileInputRef.current.value = "";
  };

  const clearChat = async () => {
    await fetch(`${API_BASE}/clear`, { method: "POST" });
    setMessages([]);
    setAudioUrl(null);
  };

  return (
    <div className="container">
      <h2>Yash's Chatbot</h2>
      {userInfo && (
        <div className="userinfo">
          <b>{userInfo.name}</b> | {userInfo.current_role}
        </div>
      )}
      <div className="chatbox">
        {messages.map((msg, i) => (
          <div key={i} className={msg.role}>
            <b>{msg.role === "user" ? "You" : "Yash"}:</b> {msg.content}
          </div>
        ))}
        {loading && <div className="assistant">Yash is typing...</div>}
      </div>
      {audioUrl && (
        <audio controls src={audioUrl} style={{ marginTop: 10 }}>
          Your browser does not support the audio element.
        </audio>
      )}
      <div className="input-row">
        <input
          type="text"
          value={input}
          disabled={loading}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === "Enter" && sendText()}
          placeholder="Type your message..."
        />
        <button onClick={sendText} disabled={loading || !input.trim()}>Send</button>
        <input
          type="file"
          accept="audio/*"
          ref={fileInputRef}
          style={{ display: "none" }}
          onChange={sendVoice}
        />
        <button onClick={() => fileInputRef.current.click()} disabled={loading}>Send Voice</button>
        <button onClick={clearChat} disabled={loading}>Clear</button>
      </div>
    </div>
  );
}

export default App;
