import React, { useState, useRef } from "react";
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import "./App.css";

const API_BASE = "http://localhost:8000"; // Change if backend runs elsewhere

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [audioUrl, setAudioUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [userInfo, setUserInfo] = useState(null);
  const [recording, setRecording] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // Speech recognition hook
  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition
  } = useSpeechRecognition();

  // Fetch user info on mount
  React.useEffect(() => {
    fetch(`${API_BASE}/user_info`)
      .then(res => res.json())
      .then(setUserInfo)
      .catch(() => {});
  }, []);

  const sendText = async (textToSend) => {
    const text = textToSend !== undefined ? textToSend : input;
    if (!text.trim()) return;
    setLoading(true);
    setMessages(msgs => [...msgs, { role: "user", content: text }]);
    setInput("");
    try {
      const res = await fetch(`${API_BASE}/chat`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ user_input: text, input_type: "text" })
      });
      const data = await res.json();
      setMessages(msgs => [...msgs, { role: "assistant", content: data.answer }]);
      setAudioUrl(data.audio_url ? `${API_BASE}${data.audio_url}` : null);
    } catch (e) {
      alert("Error communicating with server.");
    }
    setLoading(false);
  };

  const sendVoiceBlob = async (blob) => {
    setLoading(true);
    // Add a placeholder for the user's voice message
    setMessages(msgs => [...msgs, { role: "user", content: "[Voice message sent]" }]);
    const formData = new FormData();
    formData.append("file", blob, "recording.wav");
    try {
      const res = await fetch(`${API_BASE}/voice`, {
        method: "POST",
        body: formData
      });
      const data = await res.json();
      // Update the last user message with the transcribed text
      setMessages(msgs => {
        const updated = [...msgs];
        // Find the last user message with the placeholder
        for (let i = updated.length - 1; i >= 0; i--) {
          if (updated[i].role === "user" && updated[i].content === "[Voice message sent]") {
            updated[i] = { ...updated[i], content: data.answer ? data.answer.split('\n')[0] : "[Voice message]" };
            break;
          }
        }
        // Add the assistant's response
        return [...updated, { role: "assistant", content: data.answer }];
      });
      setAudioUrl(data.audio_url ? `${API_BASE}${data.audio_url}` : null);
    } catch (e) {
      alert("Error communicating with server.");
    }
    setLoading(false);
  };

  const startRecording = async () => {
    if (!navigator.mediaDevices) {
      alert("Media devices not supported in this browser.");
      return;
    }
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new window.MediaRecorder(stream);
      audioChunksRef.current = [];
      mediaRecorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          audioChunksRef.current.push(e.data);
        }
      };
      mediaRecorder.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
        sendVoiceBlob(audioBlob);
        stream.getTracks().forEach(track => track.stop());
      };
      mediaRecorderRef.current = mediaRecorder;
      mediaRecorder.start();
      setRecording(true);
    } catch (err) {
      alert("Could not start recording: " + err.message);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      setRecording(false);
    }
  };

  const clearChat = async () => {
    await fetch(`${API_BASE}/clear`, { method: "POST" });
    setMessages([]);
    setAudioUrl(null);
  };

  // Add function to delete all audio files
  const deleteAudioFiles = async () => {
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE}/delete_audio_files`, { method: "POST" });
      if (res.ok) {
        setAudioUrl(null);
        alert("All audio files deleted.");
      } else {
        alert("Failed to delete audio files.");
      }
    } catch (e) {
      alert("Error communicating with server.");
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h2>Yash's Chatbot</h2>
      {userInfo && (
        <div className="userinfo">
          <b>{userInfo.name}</b> | {userInfo.current_role}
        </div>
      )}
      <div className="chatbox">
        {messages.map((msg, i) => (
          <div key={i} className={msg.role}>
            <b>{msg.role === "user" ? "You" : "Yash"}:</b> {msg.content}
          </div>
        ))}
        {loading && <div className="assistant">Yash is typing...</div>}
      </div>
      {audioUrl && (
        <audio controls src={audioUrl} style={{ marginTop: 10 }}>
          Your browser does not support the audio element.
        </audio>
      )}
      <div className="input-row">
        <input
          type="text"
          value={input}
          disabled={loading}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === "Enter" && sendText()}
          placeholder="Type your message..."
        />
        <button onClick={() => sendText()} disabled={loading || !input.trim()}>Send</button>
        <button
          onClick={recording ? stopRecording : startRecording}
          disabled={loading}
          style={{ background: recording ? '#d9534f' : undefined }}
        >
          {recording ? "Stop" : "Record"}
        </button>
        <button onClick={clearChat} disabled={loading}>Clear</button>
        {/* Add Delete Audio Files button */}
        <button onClick={deleteAudioFiles} disabled={loading} style={{ marginLeft: 8 }}>
          Delete Audio Files
        </button>
      </div>
      {/* Live voice-to-text transcription UI */}
      <div className="transcribe-box">
        <div style={{ marginTop: 16, marginBottom: 4 }}>
          <b>Live Voice-to-Text:</b>
        </div>
        {!browserSupportsSpeechRecognition ? (
          <div>Your browser does not support speech recognition.</div>
        ) : (
          <>
            <button
              onClick={listening ? SpeechRecognition.stopListening : () => SpeechRecognition.startListening({ continuous: true })}
              style={{ background: listening ? '#d9534f' : undefined, marginRight: 8 }}
              disabled={loading}
            >
              {listening ? "Stop Transcribing" : "Start Transcribing"}
            </button>
            <button onClick={resetTranscript} disabled={loading || !transcript}>Reset</button>
            <button
              onClick={() => {
                sendText(transcript);
                resetTranscript();
              }}
              disabled={loading || !transcript.trim()}
              style={{ marginLeft: 8 }}
            >
              Send Transcript
            </button>
            <div className="transcript-area" style={{ marginTop: 8, minHeight: 32, background: '#f3f3f3', borderRadius: 6, padding: 8 }}>
              {transcript || <span style={{ color: '#aaa' }}>Transcript will appear here...</span>}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
